init_hook:
if command -v uv >/dev/null 2>&1; then 
  # Ensure Python 3.12 is available under uv (idempotent)
  uv python install 3.12 >/dev/null 2>&1 || true
  # Create a project-local venv using uv (or fallback to python -m venv)
  if [ ! -d .venv ]; then 
    uv venv .venv --python 3.12 >/dev/null 2>&1 || python -m venv .venv; 
  fi; 
  # Activate the venv by sourcing the activation script (no uv 'activate' subcommand)
  source .venv/bin/activate >/dev/null 2>&1 || true; 
fi
